import { useState, useEffect } from 'react'
import axios from 'axios'
import noteService from './services/update'

const ShowNumbers = ({personstoShow}) => {
  const personlist = personstoShow.map(person => <p key={person.name.length+Math.random(4525)}>{person.name} {person.number}</p>)
  return personlist
}

const Inputs = ({name, newName, onChange, onClick}) => <p>{name}: <input value = {newName} onChange={onChange} onClick={onClick}/></p>

const Filters = ({filter ,onChange, onClick}) => <p>filter shown with<input value ={filter} onChange={onChange} onClick={onClick}/></p>

const App = () => {
  const [persons, setPersons] = useState([])
  const [personstoShow, setPersonstoShow] = useState(persons)
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const datahook = () => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons').then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }

  useEffect(datahook, [])

  const addName = (event) => {
  event.preventDefault()
  let found = persons.find(person => person.name === newName)
  if (found === undefined) {
    const noteObject = {
        name: newName,
        number: newNumber,
    }
    setPersons(persons.concat(noteObject))
    setNewName('')
    setNewNumber('')
    setPersonstoShow(persons)
  } else {
    alert(`${newName} is allready added to phonebook`)
  }
}

  const handleNoteChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNoteClick = (event) => {
    setNewName('')
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNumberClick = (event) => {
    setNewNumber('')
  }

  const handleFilter = (event) => {
    if (event.type === 'change'){
      setFilter(event.target.value)
      if (filter === ''){
        setPersonstoShow(persons)
      } else {
        setPersonstoShow(persons.filter(person => person.name.toLowerCase().includes(filter.toLowerCase())))
      }
    }
    else if (event.type === 'click'){
      setFilter('')
      setPersonstoShow(persons)
    }
  }

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Filter by name</h2>
      <Filters value ={filter} onChange={handleFilter} onClick={handleFilter}/>
      <h2>Add New</h2>
      <form onSubmit={addName}>
        <div>
          <Inputs name={'Name'} onChange={handleNoteChange} onClick={handleNoteClick} />
          <Inputs name={'Number'} onChange={handleNumberChange} onClick={handleNumberClick} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ShowNumbers personstoShow = {personstoShow} />
      ...
    </div>
  )

}

export default App
