import { useState } from 'react'

const ShowNumbers = ({personstoShow}) => {
  const personlist = personstoShow.map(person => <p>{person.name} {person.number}</p>)
  console.log(personlist)
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])
  const [personstoShow, setPersonstoShow] = useState(persons)
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const addName = (event) => {
  event.preventDefault()
  let found = persons.find(person => person.name === newName)
  if (found === undefined) {
    const noteObject = {
        name: newName,
        number: newNumber,
    }
    setPersons(persons.concat(noteObject))
    setNewName('')
    setNewNumber('')
    setPersonstoShow(persons)
  } else {
    alert(`${newName} is allready added to phonebook`)
  }
}

  const handleNoteChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNoteClick = (event) => {
    setNewName('')
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNumberClick = (event) => {
    setNewNumber('')
  }

  const handleFilter = (event) => {
    if (event.type === 'change'){
      setFilter(event.target.value)
      if (filter === ''){
        setPersonstoShow(persons)
      } else {
        setPersonstoShow(persons.filter(person => person.name.toLowerCase().includes(filter.toLowerCase())))
      }
    }
    else if (event.type === 'click'){
      setFilter('')
      setPersonstoShow(persons)
    }
  }

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Filter by name</h2>
      filter shown with<input value ={filter} onChange={handleFilter} onClick={handleFilter}/>
      <h2>Add New</h2>
      <form onSubmit={addName}>
        <div>
          <p>Name: <input value = {newName} onChange={handleNoteChange} onClick={handleNoteClick}/></p>
          <p>Number: <input value = {newNumber} onChange={handleNumberChange} onClick={handleNumberClick}/></p>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {personstoShow.map(person => <p>{person.name} {person.number}</p>)}
      <ShowNumbers personstoShow = {personstoShow} />
      ...
    </div>
  )

}

export default App
